mapper.select:
select * from(select rownum as rnum, m.* from( select member_no as memberNo, member_name as memberName, phone, member_id as memberId, member_pw as memberPw, email, member_type as memberType, enroll_date as enrollDate, filepath, member_grade as memberGrade from member where member_type = 4
and member_id like '%'||#{id}||'%'
order by member_no desc)m order by rnum) where rnum BETWEEN #{start} and #{end}

mapper.delete:
delete from 

	
		PROJECT where project_no = #{boardNo}
	
	
		SHARE_BOARD where board_no = #{boardNo}
	
	
		ANNOUNCEMENT where announce_no = #{boardNo}
	
	
		GOSU_NOTICE where gnotice_no = #{boardNo}
	
	
		CONTEST where contest_no = #{boardNo}

mapper.select:
select count(*) 
from 

	
		PROJECT where recruit_start_date = #{date}
	
	
		SHARE_BOARD where REG_DATE like #{date}||'%'
	
	
		ANNOUNCEMENT where write_date = #{date}
	
	
		gosu_notice where gnotice_date = #{date}
	
	
		contest
		where contest_status = 2 and CONTEST_DATE = #{date}
