mapper.select:
select
member_id as gosuId
from member where member_no = #{gosuNo}

select
filepath as gosuImg
from member where member_no = #{gosuNo}

select
filepath as writeImg
from member where member_id =
#{writeId}

mapper.update:
update
gosu_feedback set
FEEDBACK_NUM = 2 where FEEDBACK_NO = #{feedbackNo}

update
gosu_feedback set
FEEDBACK_NUM = 3 where FEEDBACK_NO = #{feedbackNo}

mapper.select:
select
grr.REQUEST_REVIEW_NO as requestReviewNo, grr.REQUEST_PROJECT_SUB_NO as requestProjectSubNo, grr.REQUEST_MEMBER_ID as memberId, grr.REQUEST_REVIEW_CONTENT as requestReviewContent, grr.REQUEST_REVIEW_DATE as requestReviewDate
from gosu_request_review grr LEFT JOIN gosu_Request_project_sub sub ON grr.REQUEST_PROJECT_SUB_NO = sub.REQUEST_PROJECT_SUB_NO LEFT JOIN gosu_request_COST cost ON sub.cost_no = cost.cost_no LEFT JOIN gosu gosu ON cost.gosu_no = gosu.gosu_no where ggosu_no = #{ggsouNo}
